 function showAlert() {
   // Get the checkbox
  var checkBox = document.getElementById("agree");
     if (checkBox.checked == false){
     var myText = "Sorry, your submission is failed because you have not agreed on our consent. Please try again after clicking 'I Agree' at the bottom of the page 'Overview and Consent'.";
    alert (myText);
   }
  }


