   function disableQ5Q51 () {
     var q5 =document.getElementById("dropdown5")
     var q51 =document.getElementById("dropdown51")
     option = " Please skip this question. "
    if (document.getElementById("dropdown1").value === "1") {
        q5.disabled = true;
        q51.disabled = true;
        q5.innerHTML = option;
        q51.innerHTML = option;
      }
    else{
        q5.disabled = false;
        q51.disabled = false;
    }
}

    function invisiableQ5Q51 () {
     var q5 =document.getElementById("dropdown5")
     var q51 =document.getElementById("dropdown51")
     var p5 =document.getElementById("p5")
     var p51 =document.getElementById("p51")
    if (document.getElementById("dropdown1").value === "1") {
        q5.hidden = true;
        q51.hidden = true;
        p5.hidden = true;
        p51.hidden = true;
      }
    else{
        q5.hidden = false;
        q51.hidden = false;
        p5.hidden = false;
        p51.hidden = false;
    }
}
