function invisible_opt(id, ids){
    const section_id = id.split('_')[0];
    if (document.getElementById(id).value === "1") {
        ids.forEach(x => { hide_selection(section_id.concat("_dropdown", x));
                            document.getElementById(section_id.concat("_qt", x)).hidden = true;});
      }
    else{
        let new_ids = ids.slice(0,2);
        new_ids.forEach(x => {  unhide_selection(section_id.concat("_dropdown", x));
                                document.getElementById(section_id.concat("_qt", x)).hidden = false;});
    }
}

function follow_up(id){
    const section_id = id.split('_')[0];
    const ques_num = id.split('dropdown')[1];
    const array1 = ["0", "1"];
    var confidence_title =document.getElementById(section_id.concat("_qt", ques_num, "1"));
    var confidence_option_id = id.concat("1");

    var question_title =document.getElementById(section_id.concat("_qt", ques_num, "2"));
    var question_option_id =id.concat("2");

    if (document.getElementById(id).value ==="2"){
        question_title.hidden = false;
        unhide_selection(question_option_id);
        confidence_title.hidden = true;
        hide_selection(confidence_option_id);
    }
    else if (array1.includes(document.getElementById(id).value)){
        question_title.hidden = true;
        confidence_title.hidden = false;
        hide_selection(question_option_id);
        unhide_selection(confidence_option_id);
    }
}

function showAlert() {
    const checkBox_ids = ["checkbox_con", "checkbox_ins", "checkbox_tut"];
    const page_names = ["1.Consent", "2.Instructions", "3.Tutorial"];
    var flag = true;
    var page = ""
    for(let i=0; i<checkBox_ids.length; i++){
        if (document.getElementById(checkBox_ids[i]).checked === false ){
            flag = false;
            if (page === ""){
                page = page.concat("\"", page_names[i], "\"");
            }
            else{
                page = page.concat(" and ", "\"", page_names[i],  "\"");
            }
        }
    }
    var prefix = "the page of ";
    if (page.includes("and")){
        prefix = "pages include ";
    }
    if (flag === false){
        var myText = "Sorry, your submission has failed because you have not checked the required checkbox at the bottom of ".concat(prefix, page, ".");
        alert (myText);
    }

 }
