body.script:
function get_ready(){
    const checkBox_ids = document.querySelectorAll('[id^="checkbox_"]');
    const all_in_tutorial = document.querySelectorAll('[id^="dropdownT"]');
    checkBox_ids.forEach(element => {element.required = false});
    all_in_tutorial.forEach(element => {element.required = false});
}
function tutorial_b (ques_id, ans_correct) {
        const ques_num = ques_id.split('dropdownT')[1];
        var question = document.getElementById(ques_id);
        var pop_id = "pp".concat(ques_num).concat(question.value);
        document.getElementById(pop_id).classList.toggle("show");
        question.value = ans_correct;
        question.disabled = true;
}

function tutorial_a (ques_id,ans) {
        const ques_num = ques_id.split('dropdownT')[1];
        var pop_id = "pp".concat(ques_num);
        document.getElementById(pop_id).classList.toggle("show");
}


function invisible (id, id1, id2) {
     var q1 =document.getElementById("dropdown".concat(id1));
     var q2 =document.getElementById("dropdown".concat(id2));
     var p1 =document.getElementById("qt".concat(id1));
     var p2 =document.getElementById("qt".concat(id2));
     const a_ids = [q1, q2, p1, p2]
    if (document.getElementById(id).value === "1") {
        a_ids.forEach(element => {element.hidden = true});
        a_ids.forEach(element => {element.required = false});
      }
    else{
        a_ids.forEach(element => {element.hidden = false});
        a_ids.forEach(element => {element.required = true});
    }
}

 function showAlert() {
   // Get the checkbox
  var checkBox = document.getElementById("checkbox_all");
  const checkBox_ids = ["checkbox_ch", "checkbox_ins", "checkbox_tut"];
     if (!(checkBox.checked === true || checkBox_ids.forEach(e => {document.getElementById(e).checked === true}))){
     var myText = "Sorry, your submission is failed because you have not checked all requried checked boxes. Please go to 'ReadMe' page and follow the guidance then resubmit.";
    alert (myText);
   }
  }
